import {createAsyncThunk, createSlice} from '@reduxjs/toolkit'
import {client} from "../../api/client";

// This is managed by extra reducers
export const queryIndex = createAsyncThunk('documents/query',
    async query => {
        const response = await client.get('https://pokeapi.co/api/v2/pokemon/'+query)
        return response.data
    })

const documentsSlice = createSlice({
    name: 'documents',
    reducers: {
        documentsQuery(state, action) {
            state.query = action.payload.query
        },
        documentDetail(state, action) {
            state.selected = action.payload.selected
        }
    },
    extraReducers: {
        [queryIndex.pending]: (state, action) => {
            state.status = 'loading'
        },
        [queryIndex.fulfilled]: (state, action) => {
            state.status = 'succeeded'
            // Add any fetched documents hits to the array
            state.results = action.payload
        }
    }
})
// selectors
export const selectDocuments = state => state.documents.results
// selectors detail
export const selectDetail = state => state.documents.selected
// selectors status
export const selectorStatus = state => state.documents.status
// actions
export const { documentsQuery, documentDetail } = documentsSlice.actions
export default documentsSlice.reducer
