import {createAsyncThunk, createSlice} from '@reduxjs/toolkit'
import {client} from "../../api/client";

// Etat par défaut du Slice
const etatInitial = {
    resultats : [],
    /*numeroPokemon: '0',
    status: 'inactif'*/
}

export const requeteIndex = createAsyncThunk('pokemons/resultats',
    async requete => {
    const reponse = await client.get('https://pokeapi.co/api/v2/pokemon?limit=10&offset=' + requete)
        return reponse.results
    })

const pokemonSlice = createSlice({
    name: 'pokemons',
    etatInitial,
    reducers: {
        pokemonNumeroRequete(state, action){
            /*state.numeroPokemon = action.payload.numeroPokemon*/
        }
    },
    extraReducers: {
        [requeteIndex.pending]: (state, action) => {
            state.status = 'en cours'
        },
        [requeteIndex.fulfilled]: (state, action) => {
            state.status = 'succés'
            state.resultats = action.payload
        }
    }
})

export const selectPokemon = state => state.pokemons.resultats

/*
export const selectorStatus = state => state.pokemons.status

export const selectNumeroPokemon = state => state.pokemons.numeroPokemon
*/

export const { pokemonNumeroRequete } = pokemonSlice.actions

export default pokemonSlice.reducer
