import {createAsyncThunk, createSlice} from '@reduxjs/toolkit'
import {client} from "../../api/client";

// Etat par défaut du Slice
const etatInitial = {
    resultats : [
        {id: 1, name: 'Bubizarre'},
        {id: 2, name: 'Salameche'},
        {id: 3, name: 'Tiplouf'},
    ],
    numeroPokemon: '0',
    status: 'inactif'
}

export const requeteIndex = createAsyncThunk('pokemon/requete',
    async requete => {
    const reponse = await client.get('https://pokeapi.co/api/v2/pokemon/pokemon?limit=10&offset=' + etatInitial.numeroPokemon)
        return reponse
    })

const pokemonSlice = createSlice({
    name: 'pokemon',
    etatInitial,
    reducers: {
        pokemonRequete(state, action){
            state.numeroPokemon = action.payload.numeroPokemon
        }
    },
    extraReducers: {
        [requeteIndex.pending]: (state, action) => {
            state.status = 'en cour'
        },
        [requeteIndex.fulfilled]: (state, action) => {
            state.status = 'succés'
            state.resultats = action.payload
        }
    }
})

export const selectPokemon = state => state.pokemon.resultats

export const selectorStatus = state => state.pokemon.status

export const { pokemonRequete } = pokemonSlice.actions

export default pokemonSlice.reducer
