import React, {useEffect} from "react";
import {useDispatch} from "react-redux";
import {requetePokemonId, selectDetailPokemonAleatoire} from "./pokemonsSlice";
import { useSelector } from "react-redux";

export const PokemonRandom = () => {
    const pokemon = useSelector(selectDetailPokemonAleatoire)

    // dispatch
    const dispatch = useDispatch()

    // Fonction permettant de générer un nombre aléatoire
    function getRandomArbitrary(min, max) {
        return Math.random() * (max - min) + min;
    }

    // Limitation à 898 nombres car c'est le nombre de pokémon dans l'api
    const index = Math.round(getRandomArbitrary(1, 898))

    useEffect(() => {
        dispatch(requetePokemonId(index))
    })

    return(
        <div className={"col-sm-7"}>
            <h2>Pokémon aléatoire</h2>
            <div className={"card-detail-pokemon"}>
                <img className={"card-detail-pokemon-image"} src={"https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/" + pokemon.id + ".png"}/>
                <h2 className={"card-detail-pokemon-nom"}>{pokemon.name}</h2>
            </div>
        </div>
    );
}
