import {requeteIndex, selectNumeroPokemon, selectorStatus, selectPokemon} from "./pokemonsSlice";
import {useDispatch, useSelector} from "react-redux";
import {useEffect} from "react";

export const PokeListe = () => {
    // Récupération des pokémons
    const pokemons = useSelector(selectPokemon)
    const numeroPokemons = useSelector(selectNumeroPokemon)

    // dispatch
    const dispatch = useDispatch()
    // statut de la requête
    const status = useSelector(selectorStatus)

    const image = (urlImage) => {
        let image = urlImage.split('/')
        return image[5]
    };

    const renderedPokemon = pokemons.map(pokemon => (
        <div className={"card-pokemon-liste"}>
            <img src={"https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/" + image(pokemon.url) + ".png"}/>
            <button className={"bouton-pokemon-liste"}>{pokemon.name}</button>
        </div>
    ))

    useEffect( () => {
        if (status === 'inactif') {
            dispatch(
                requeteIndex(numeroPokemons)
            )
        }
    })

    return(
        <div className="App container-fluid">
            <h1>Poké liste</h1>
            <h2>Liste des pokémons :</h2>
            {pokemons.length !== 0
                ?
                <div className={"liste-poke-liste"}>
                    {renderedPokemon}
                </div>
                :<div></div>
            }
        </div>
    );
}
