import {requeteIndex, selectNumeroPokemon, selectorStatus, selectPokemon} from "./pokemonsSlice";
import {useDispatch, useSelector} from "react-redux";
import {useEffect} from "react";
import {DetailPokemon} from "./DetailPokemon";

export const PokeListe = () => {
    // Récupération des pokémons
    const pokemons = useSelector(selectPokemon)
    let numeroPage = 1

    // dispatch
    const dispatch = useDispatch()
    // statut de la requête
    const status = useSelector(selectorStatus)

    useEffect( () => {
        if (status === 'inactif'){
            dispatch(requeteIndex('0'))
        }
    })

    /*const pageSuivante = async() => {
        numeroPage = numeroPage + 1
    }*/

    const image = (urlImage) => {
        let image = urlImage.split('/')
        let resultatImage = image[6]
        return resultatImage
    };

    const renderedPokemon = pokemons.map(pokemon => (
        <div className={"card-pokemon-liste"}>
            <img className={"image-pokemon"} src={"https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/" + image(pokemon.url) + ".png"}/>
            <button key={pokemon.name} className={"bouton-pokemon-liste"}>{pokemon.name}</button>
        </div>
    ))

    return(
        <div className="App container-fluid">
            <h1>Poké liste</h1>
            <h2>Liste des pokémons :</h2>
            <div className={"row"}>
                <div className={"col-sm-7"}>
                    <div className={"bouton-nav-liste-pokemon"}>
                        <button className={"bouton-changer-page"}>Précédent</button>
                        <p className={"numero-page"}>{numeroPage}</p>
                        <button className={"bouton-changer-page"}>Suivant</button>
                    </div>
                    {pokemons.length !== 0
                        ?
                        <div className={"liste-poke-liste"}>
                            {renderedPokemon}
                        </div>
                        :<div></div>
                    }
                </div>
                <div className={"col-sm-5"}>
                    <DetailPokemon />
                </div>
            </div>

        </div>
    );
}
