import React from "react";
import { useSelector } from "react-redux";
import {selectDetailPokemonCible, selectorStatusPokemonCible} from "./pokemonsSlice";

export const DetailPokemon = () => {
    const status = useSelector(selectorStatusPokemonCible)

    const pokemon = useSelector(selectDetailPokemonCible)



    return(
        <div>
            <h2>Nom du Pokémon</h2>
            {status !== 'rien'
                ?
                <p>{pokemon.name}</p>
                :
                <p>Aucun pokémon sélectionné</p>
            }
        </div>
    );
}
