import React from "react";
import { useSelector } from "react-redux";
import {selectDetailPokemonCible, selectorStatusPokemonCible} from "./pokemonsSlice";

export const DetailPokemon = () => {
    const state = useSelector(selectorStatusPokemonCible)

    const pokemon = useSelector(selectDetailPokemonCible)

    const statistiques = []

    for (const stat of pokemon.stats.map()){
        statistiques.push(stat.base_stat + ' : ' + stat.stat.name)
    }

    const affichageDetailPokemon = (
        <div className={"card-detail-pokemon"}>
            <img className={"card-detail-pokemon-image"} src={"https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/" + pokemon.id + ".png"}/>
            <h2 className={"card-detail-pokemon-nom"}>{pokemon.name}</h2>
            { statistiques }
        </div>
    )

    /*const listeStats = pokemon.stats.map(stat => (
        <p>{ stat.stat.name } : { stat.base_stat }</p>
    ))*/


    return(
        <div>
            {state !== 'rien'
                ? affichageDetailPokemon
                : <p>Aucun pokémon sélectionné</p>
            }
        </div>
    );
}
